import React, { useState, useEffect, useCallback } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react"; // Using lucide-react for icons

const slides = [
  "data:image/jpeg;base64,",
  "https://maytinhsocbay.com/wp-content/uploads/2023/02/may-tinh-pc-gaming-banner-top.jpg",
  "https://cdn-media.sforum.vn/storage/app/media/ctv_seo8/h%C3%ACnh%20n%E1%BB%81n%20apple%20watch/hinh-nen-apple-watch-28.jpg",
];

const HeroCarousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = useCallback(() => {
    setCurrentSlide((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
  }, []);

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
  };

  useEffect(() => {
    const slideInterval = setInterval(nextSlide, 5000);
    return () => clearInterval(slideInterval);
  }, [nextSlide]);

  return (
    <section className="bg-gray-100 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto">
        <div className="relative w-full" style={{ paddingBottom: "33.33%" }}>
          <div className="absolute top-0 left-0 w-full h-full overflow-hidden rounded-lg">
            <div
              className="flex transition-transform duration-700 ease-in-out h-full"
              style={{ transform: `translateX(-${currentSlide * 100}%)` }}
            >
              {slides.map((slide, index) => (
                <div key={index} className="w-full flex-shrink-0 h-full">
                  <img
                    src={slide}
                    alt={`Slide ${index + 1}`}
                    className="w-full h-full object-cover"
                  />
                </div>
              ))}
            </div>
          </div>

          <button
            onClick={prevSlide}
            className="absolute top-1/2 left-2 md:left-4 transform -translate-y-1/2 bg-white/60 dark:bg-gray-800/60 p-2 rounded-full hover:bg-white dark:hover:bg-gray-800 shadow-md transition-colors focus:outline-none"
            aria-label="Previous Slide"
          >
            <ChevronLeft className="w-5 h-5 md:w-6 md:h-6 text-gray-800 dark:text-gray-200" />
          </button>
          <button
            onClick={nextSlide}
            className="absolute top-1/2 right-2 md:right-4 transform -translate-y-1/2 bg-white/60 dark:bg-gray-800/60 p-2 rounded-full hover:bg-white dark:hover:bg-gray-800 shadow-md transition-colors focus:outline-none"
            aria-label="Next Slide"
          >
            <ChevronRight className="w-5 h-5 md:w-6 md:h-6 text-gray-800 dark:text-gray-200" />
          </button>

          <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentSlide(index)}
                className={`w-2 h-2 rounded-full transition-all duration-300 ${
                  index === currentSlide
                    ? "bg-white dark:bg-gray-800 scale-125"
                    : "bg-white/50 dark:bg-gray-800/50"
                }`}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroCarousel;
